import React from 'react';
import { Button} from './Button';
import { Link } from 'react-router-dom';
import './HeroSection.css'

// 1. Hi, my name is Zhiyue Gao, and I usually go by Ryan
// 2. Hi I am a graduate student of MENG of ECE at UWaterloo
// 3. Hi, I am a alumni of the University of Maryland, and I got my bachelor degree of computer science there
// 4. Hi, How are you
// 5. waht's up

function HeroSection({
    lightBg, topLine, lightText, lightTextDesc, headline, description, buttonLabel, img, alt, imgStart
}) {
    return (
        <>
          <div className = { lightBg ? 'home__hero-section' : 'home__hero-section darkBg'}>
            <div className="container">
                <div className="row home__hero-row" 
                style = {{display: 'flex', flexDirection: imgStart === 'start' ? 'row-reverse' : 'row'}}
                >
                    <div className="rol">
                        <div className="home__hero-text-wrapper">
                            <div className="top-line">
                                {topLine}
                                <h1 className = {lightText ? 'heading' : 'heading dark'}>{headline}</h1>
                                <p className={lightTextDesc ? 'home__hero-subtitle' : 'home__hero-subtitle dark'}>
                                    {description}
                                </p>
                                <Link to = '/sign-up'>
                                    <Button buttonSize='btn--wide' buttonColor='blue'>{buttonLabel}</Button>
                                </Link>
                            </div>
                        </div>
                        <div class="col">
                            <div class="home__hero-img-wrapper">
                                <img src={img} alt={alt} class="home__hero-img"/>
                            </div>
                        </div>
                    </div>   
                </div>
            </div>
          </div>   
        </>
    );
}

export default HeroSection
